import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  // Fetch books from the backend
  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5001/Bookstore/Books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  // Handle ordering a book
  const handleOrderBook = (bookId) => {
    console.log(`Order book with ID: ${bookId}`);
    // Add your order book logic here
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Online Bookstore</h1>
      </header>

      <main className="main-content">
        <section className="books-section">
          <h2>Books Available</h2>
          <div className="books-list">
            {books.length === 0 ? (
              <p>No books available</p>
            ) : (
              books.map((book) => (
                <div className="book-item" key={book.BookID}>
                  <h3>{book.Title}</h3>
                  <p><strong>ISBN:</strong> {book.ISBN}</p>
                  <p><strong>Publisher:</strong> {book.Publisher}</p>
                  <p><strong>Publication Year:</strong> {book.PublicationYear}</p>
                  <p><strong>Edition:</strong> {book.Edition}</p>
                  <p><strong>Price:</strong> ${book.Price?.toFixed(2) || 'N/A'}</p>
                  <p><strong>Stock:</strong> {book.Stock}</p>
                  <button onClick={() => handleOrderBook(book.BookID)}>Order Book</button>
                </div>
              ))
            )}
          </div>
        </section>
      </main>
    </div>
  );
};

export default App;
