import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [books, setBooks] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchBooks();
    fetchUser();  
  }, []);

  // Fetch books from the database
  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  // Fetch user information (assuming user is logged in)
  const fetchUser = async () => {
    try {
      const response = await axios.get('http://localhost:5000/users/1'); // Example user ID
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  // Add book to cart
  const addToCart = (book) => {
    setCart([...cart, { ...book, quantity: 1 }]);
  };

  // Remove book from cart
  const removeFromCart = (bookId) => {
    setCart(cart.filter((item) => item.BookID !== bookId));
  };

  // Create an order
  const createOrder = async () => {
    try {
      const totalAmount = cart.reduce((acc, item) => acc + item.Price * item.quantity, 0);
      const order = {
        userID: user.UserID, // Use the logged-in user ID
        totalAmount,
        status: 'Pending',
        books: cart.map((item) => ({ bookID: item.BookID, quantity: item.quantity, subtotal: item.Price * item.quantity })),
      };

      const response = await axios.post('http://localhost:5000/orders', order);
      setOrders([...orders, response.data]);
      setCart([]);
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Online Bookstore</h1>
        {user && <h2>Welcome, {user.FirstName} {user.LastName}</h2>}
      </header>

      <main className="main-content">
        <section className="books-section">
          <h2>Books Available</h2>
          <div className="books-list">
            {books.map((book) => (
              <div className="book-item" key={book.BookID}>
                <h3>{book.Title}</h3>
                <p>Publisher: {book.Publisher}</p>
                <p>Price: ${book.Price}</p>
                <p>Stock: {book.Stock}</p>
                <button onClick={() => addToCart(book)} disabled={book.Stock <= 0}>Add to Cart</button>
              </div>
            ))}
          </div>
        </section>

        <section className="cart-section">
          <h2>Your Cart</h2>
          {cart.length === 0 ? (
            <p>Your cart is empty</p>
          ) : (
            <div className="cart-list">
              {cart.map((item) => (
                <div className="cart-item" key={item.BookID}>
                  <h3>{item.Title}</h3>
                  <p>Quantity: {item.quantity}</p>
                  <p>Subtotal: ${(item.Price * item.quantity).toFixed(2)}</p>
                  <button onClick={() => removeFromCart(item.BookID)}>Remove from Cart</button>
                </div>
              ))}
              <button onClick={createOrder}>Place Order</button>
            </div>
          )}
        </section>

        <section className="orders-section">
          <h2>Your Orders</h2>
          {orders.length === 0 ? (
            <p>No orders placed yet</p>
          ) : (
            <div className="orders-list">
              {orders.map((order) => (
                <div className="order-item" key={order.OrderID}>
                  <h3>Order ID: {order.OrderID}</h3>
                  <p>Total Amount: ${order.TotalAmount}</p>
                  <p>Status: {order.Status}</p>
                </div>
              ))}
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default App;
