import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const HomePage = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  // Fetch books from the backend
  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5001/Bookstore/Books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  // Handle ordering a book
  const handleOrderBook = (bookId) => {
    console.log(`Order book with ID: ${bookId}`);
    // Add your order book logic here
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Online Bookstore</h1>
      </header>

      <main className="main-content">
        <section className="books-section">
          <h2>Books Available</h2>
          <div className="books-table-container">
            <table className="books-table">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>ISBN</th>
                  <th>Publisher</th>
                  <th>Publication Year</th>
                  <th>Edition</th>
                  <th>Price</th>
                  <th>Stock</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {books.length === 0 ? (
                  <tr>
                    <td colSpan="8">No books available</td>
                  </tr>
                ) : (
                  books.map((book) => (
                    <tr key={book.BookID}>
                      <td>{book.Title}</td>
                      <td>{book.ISBN}</td>
                      <td>{book.Publisher}</td>
                      <td>{book.PublicationYear}</td>
                      <td>{book.Edition}</td>
                      <td>${book.Price}</td>
                      <td>{book.Stock}</td>
                      <td>
                        <button onClick={() => handleOrderBook(book.BookID)} className="order-button">Order Book</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </section>
      </main>
    </div>
  );
};

export default HomePage;
