import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showErrorMessage, setShowErrorMessage] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    handleLogin();
  };

  const handleLogin = async () => {
    try {
      const response = await fetch(`http://localhost:3000/userAccounts/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      });

      if (response.ok) {
        const userAccount = await response.json();
        console.log(userAccount);
        sessionStorage.setItem('userId', userAccount.UserID);
        sessionStorage.setItem('username', userAccount.Username);
        navigate('/landingpage');
      } else {
        setShowErrorMessage(true);
        throw new Error('Invalid username or password');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      setShowErrorMessage(true);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
      {showErrorMessage && <p>Invalid username or password</p>}
    </div>
  );
};

export default LoginPage;
