import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  // Fetch books from the backend
  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5001/Bookstore/Books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  // Handle ordering a book
  const handleOrderBook = (bookId) => {
    console.log(`Order book with ID: ${bookId}`);
    // Add your order book logic here
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Online Bookstore</h1>
      </header>

      <main className="main-content">
        <section className="books-section">
          <h2>Books Available</h2>
          <div className="auth-form-container">
            <table style={{ width: '100%', marginBottom: '10px', borderSpacing: '10px 20px' }}>
              <thead>
                <tr key="header">
                  <th style={{ fontSize: '20px', color: "black", fontWeight: 'bold', textAlign: 'left' }}>Title</th>
                  <th style={{ fontSize: '20px', color: "black", fontWeight: 'bold', textAlign: 'left' }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {books.length === 0 ? (
                  <tr>
                    <td colSpan="2">No books available</td>
                  </tr>
                ) : (
                  books.map((book) => (
                    <tr key={book.BookID}>
                      <td style={{ textAlign: 'left' }}>{book.Title}</td>
                      <td style={{ textAlign: 'left' }}>
                        <button onClick={() => handleOrderBook(book.BookID)} style={{ fontSize: '12px', color: "black", marginRight: '10px', padding: '5px 10px' }}>Order Book</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </section>
      </main>
    </div>
  );
};

export default App;
